<template>
  <div
    id="landing"
    class="w-screen h-screen bg-img bg-cover flex flex-col sm:flex-row sm:flex-nowrap gap-y-2 md:space-y-0 lg:grid-cols-2 sm:gap-x-4 lg:gap-x-40 items-center justify-center"
  >
    <Splash />
    <Login @display-signup="DisplaySignUp" />
    <Signup v-if="displaySignUp" @hide-modal="HideSignUp" />
  </div>
</template>

<script>
// @ is an alias to /src
import Splash from "@/components/Splash.vue";
import Login from "@/components/Login.vue";
import Signup from "@/components/Signup.vue";
import { ref } from "vue";

export default {
  name: "LandingView",
  components: {
    Splash,
    Login,
    Signup,
  },
  setup() {
    const displaySignUp = ref(false);

    const DisplaySignUp = () => {
      displaySignUp.value = true;
    };
    const HideSignUp = () => {
      displaySignUp.value = false;
    };

    return {
      displaySignUp,
      DisplaySignUp,
      HideSignUp,
    };
  },
};
</script>

<style scoped>
.bg-img {
  background: url("data:image/svg+xml;base64,");
}
</style>
